INSERT INTO public.pdf_property (id, text, x, y, font, size, pdf_document_id, order_number, center, rights, custom) VALUES (1214, 'Білім алғаны үшін төлемақы жыл басында немесе оқу жылы кезінде тәртібін анықтайтын ҚР үкіметінің Қаулысына сәйкес, ҚР Білім және ғылым министрлігінің бұйрықтарына сәйкес, ҚР үкіметінің Қаулысына сәйкес университет қызметкерлерінің жалақысы көбейген жағдайда, белгіленген тәртіпте коммуналдық қызмет тарифтерінің өсуіне және БІЛІМ БЕРУ ҰЙЫМЫНЫҢ еркінен тыс болған жағдайларға байланысты өсуі мүмкін.', 9, 0, 'Normal', 12, 180, 196.00, false, false, false);
